document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll("[data-ark-acc]").forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault(),function(e){const t=e.currentTarget,a=t.parentNode,n=t.nextElementSibling,r=t.lastElementChild;"false"===a.getAttribute("aria-expanded")?(a.setAttribute("aria-expanded","true"),n.setAttribute("aria-hidden","false"),r.setAttribute("data-opened","true")):(a.setAttribute("aria-expanded","false"),n.setAttribute("aria-hidden","true"),r.setAttribute("data-opened","false"))}(e)}))}))}));